->view(THEME . 'layout/inc-header'); ?>    </header>    <?php    $menuLayout = 'layout/inc-menu-new-dashboard';    $leftColClass = ' col-lg-3 pad-top20 ';    $contColClass = ' col-lg-9 pad-left30 pad-top20 ';    if (uri_string() == 'dashboard') {        $menuLayout = 'layout/inc-menu';        $leftColClass = ' col-lg-5 pad-top20 ';        $contColClass = ' col-lg-7 pad-left30 pad-top20 ';    }    ?>    <section id='main-content'>        <div class="container">            <div class="content-main">                                    <div class="row content-bg">                    <div class="col-sm-12">                         <?php                        $this->load->view(THEME . 'messages/inc-messages');                        ?>                    </div>                                        <div id="left-column" class="<?= $leftColClass; ?>">                        <div class="col-lg-12 menu padding-0">                            <?php $this->load->view(THEME . $menuLayout); ?>                        </div>                        <div class="col-lg-12 menu padding-0">                            <?php //$this->load->view(THEME . 'layout/inc-notifications');  ?>                        </div>                    </div>                                        <div  class="<?= $contColClass; ?> " >                        <?php echo $content; ?>                        <div class="clearfix"></div>                    </div>                                        <div class="col-lg-12 order-map">                        <h2>Order Map</h2>                                                <div id="map" style="width: 100%; height: 400px;"></div>                                                <script>                            // Define your locations: HTML content for the info window, latitude, longitude                            var locations = JSON.parse('<?php echo $orderdata ?>');                            // Setup the different icons and shadows                            var iconURLPrefix = 'http://maps.google.com/mapfiles/ms/icons/';                            var icons = [                                iconURLPrefix + 'red-dot.png',                                iconURLPrefix + 'green-dot.png',                                iconURLPrefix + 'blue-dot.png',                                iconURLPrefix + 'orange-dot.png',                                iconURLPrefix + 'purple-dot.png',                                iconURLPrefix + 'pink-dot.png',                                iconURLPrefix + 'yellow-dot.png'                            ]                            var iconsLength = icons.length;                            var map = new google.maps.Map(document.getElementById('map'), {                                zoom: 0,                                center: new google.maps.LatLng(-37.92, 151.25),                                mapTypeId: google.maps.MapTypeId.ROADMAP,                                mapTypeControl: false,                                streetViewControl: false,                                panControl: false,                                zoomControlOptions: {                                    position: google.maps.ControlPosition.LEFT_BOTTOM                                }                            });                            var infowindow = new google.maps.InfoWindow({                                maxWidth: 160                            });                            var markers = new Array();                            var iconCounter = 0;                            // Add the markers and infowindows to the map                            for (var i = 0; i < locations.length; i++) {                                var marker = new google.maps.Marker({                                    position: new google.maps.LatLng(locations[i][1], locations[i][2]),                                    map: map,                                    icon: icons[iconCounter]                                });                                markers.push(marker);                                google.maps.event.addListener(marker, 'click', (function (marker, i) {                                    return function () {                                        infowindow.setContent(locations[i][0]);                                        infowindow.open(map, marker);                                    }                                })(marker, i));                                iconCounter++;                                // We only have a limited number of possible icon colors, so we may have to restart the counter                                if (iconCounter >= iconsLength) {                                    iconCounter = 0;                                }                            }                            function autoCenter() {                                //  Create a new viewpoint bound                                var bounds = new google.maps.LatLngBounds();                                //  Go through each...                                for (var i = 0; i < markers.length; i++) {                                    bounds.extend(markers[i].position);                                }                                //  Fit these bounds to the map                                map.fitBounds(bounds);                            }                            autoCenter();                        </script>                     </div>                </div>            </div>        </div>    </section>    <section id='footer'>        <?php $this->load->view(THEME . 'layout/inc-footer'); ?>    </section>    <?php    $this->load->view(THEME . 'layout/inc-before-body-close');    echo $CI->assets->renderFooter();    ?></body></html>